mapper.select:
SELECT *
FROM manager
WHERE mng_id = #{mng_id} AND mng_pwd = #{mng_pwd}


SELECT mng_num, mng_id, mng_pwd, mng_name, mng_lev, mng_email, mng_phone
FROM (
	SELECT /*+ INDEX_ASC(manager pk_manager) */
	rownum as rnum, mng_num, mng_id, mng_pwd, mng_name, mng_lev, mng_email, mng_phone
	FROM manager
	WHERE 



	 rownum <= #{pageNum} * #{amount}
		) adminList
		WHERE rnum > (#{pageNum}-1) * #{amount}
