mapper.select:
SELECT c_no, c_title, c_category, c_writer, c_comment, c_img, c_readcnt, to_char(c_reg_date,'YYYY-MM-DD') as c_reg_date,m_num
FROM COMMUNITY
where c_no = #{c_no}

SELECT c_no, c_title, c_category, c_writer, c_comment, c_img, c_readcnt, to_char(c_reg_date,'YYYY-MM-DD') as c_reg_date,m_num
FROM COMMUNITY
where c_no = #{c_no}

mapper.update:
UPDATE COMMUNITY SET c_title = #{c_title}, c_category = #{c_category}, c_comment = #{c_comment},
c_img = #{c_img},
c_reg_date = to_date(sysdate,'yyyy-mm-dd') WHERE c_no = #{c_no}

UPDATE COMMUNITY
SET c_readcnt = c_readcnt + 1
WHERE c_no = #{c_no}
