mapper.select:
		select i_num, i_parts, i_name, i_price, i_quan, i_img
		from (select /*+ index_desc(items PK_ITEMS) */ 
				rownum as rnum, i_num, i_parts, i_name, i_price, i_quan, i_img
      		  from items
      		  where



		 rownum <= #{pageNum} * #{amount}
				) itemsList
		where rnum > (#{pageNum}-1) * #{amount}

select i_num, i_parts, i_name, i_price, i_quan, i_img
from items
